SACArs=getDoctorInfoSAC.Body.getDoctorsInfoResponse.availableResource
numberSACArs=SACArs.size()

 mySet = new HashSet<>();

//проверить, все ли ресурсы в ответе sac уникальные
if (numberSACArs>0){
	for(int i=0; i<numberSACArs; i++){
		for ( x : SACArs[i].id) {
   			 mySet.add(x);
		}
	}
}

SACArsWithRoom=[]
